  name: "image-server",
  version: "1.0.0",
  capabilities: {
    tools: {
      generate_image: {
        description: "A tool that returns a red circle image",
        parameters: {
          type: "object",
          properties: {},
          required: [],
        },
      },
    },
  },
});

