#!/bin/bash log() { echo -e "\033[0;32m[LOG]\033[0m $1"; } warn() { echo -e "\033[1;33m[WARN]\033[0m $1"; } configure_gemini_cli_mcp() { local target_dir="$1" local server_script_rel_path=".cursor/mcp/mcp-commit-server/server.js" local memory_bank_script_rel_path=".cursor/mcp/memory-bank-mcp/server.js" log "Testing Gemini CLI configuration..." if [[ ! -f "$target_dir/$server_script_rel_path" ]]; then warn "MyMCP server script missing at $target_dir/$server_script_rel_path. Skipping." return 1 fi if [[ ! -f "$target_dir/$memory_bank_script_rel_path" ]]; then warn "MemoryBankMCP server script missing at $target_dir/$memory_bank_script_rel_path. Skipping." return 1 fi echo "SUCCESS: All server scripts found" return 0 } echo "=== Testing with missing server scripts ===" configure_gemini_cli_mcp "." echo "Exit code: $?"
